create table if not exists users(id bigserial primary key,email text unique not null,name text,role text default 'agent',password_hash text not null,created_at timestamp default now()); create table if not exists tags(id bigserial primary key,name text unique not null); create table if not exists quick_replies(id bigserial primary key,key text unique not null,text text not null); create table if not exists notes(id bigserial primary key,user_id bigint references users(id) on delete cascade,text text not null,created_at timestamp default now());